import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import CardFeature from "./CardFeature";
import FilterProduct from "./FilterProduct";

const AllProduct = ({ heading }) => {
  const productData = useSelector((state) => state.product.productList);
  const categoryList = [...new Set(productData.map((el) => el.category))];

  console.log(productData)
  //filter data display
  const [filterby, setFilterBy] = useState("");
  const [dataFilter, setDataFilter] = useState([]);

  useEffect(() => {
    setDataFilter(productData);
  }, [productData]);

  const handleFilterProduct = (category) => {

     if(category==="ALL"){
      //  const res =  fetch(`https://food-backend-mern.onrender.com/product`)
      //  const resData =  res.json()
       
       setDataFilter(() => {
         return [productData];
       });
     }
   else{
       setFilterBy(category)
       const filter = productData.filter(
         (el) => el.category.toLowerCase() === category.toLowerCase()
       );
       setDataFilter(() => {
         return [...filter];
       });
   }
  };

  const loadingArrayFeature = new Array(10).fill(null);

  return (
    <div className="my-5">
      <h2 className="font-bold text-2xl text-slate-800 mb-4">{heading}</h2>
      <h2 className="font-bold text-2xl text-slate-800 mb-4" onClick={() => handleFilterProduct("All")}>All</h2>
      <div className="flex gap-4 justify-center overflow-scroll scrollbar-none">
     
        {categoryList[0] ? (
          categoryList.map((el) => {
            return (
              <FilterProduct
                category={el}
                key={el}
                isActive={el.toLowerCase() === filterby.toLowerCase()}
                onClick={() => handleFilterProduct(el)}
              />
            );
          })
        ) : (
            <div className="spinner-border  text-danger" style={{width: '6rem', height: '6rem'}} role="status">
                <span class="visually-hidden"></span>
              </div>

         /* <div className="min-h-[150px] flex justify-center items-center">
            <p>Loading...</p>        
          </div>*/

        )}
      </div>

      <div className="flex flex-wrap justify-center gap-4 my-4">
        {dataFilter[0]
          ? dataFilter.map((el) => {
              return (
                <CardFeature
                  key={el._id}
                  id={el._id}
                  image={el.image}
                  name={el.name}
                  category={el.category}
                  price={el.price}
                />
              );
            })
          : 
          loadingArrayFeature.map((el,index) => (
              <CardFeature loading="Loading..." key={index+"allProduct"} />
            ))}
      </div>
    </div>
  );
};

export default AllProduct;
